sys_variable_value.value:
(function execute(inputs, outputs) {
    // parse entries, create records in mid_profile_config
    // GR for the settings
    var settingGr = new GlideRecord('mid_profile_config');
    var profileGr = new GlideRecord('mid_server_profile');
  	var config=gs.xmlToJSON(inputs.configXml);

    for (var i = 0; i < (config['parameters']['parameter']).length; i++) {

          	gs.info("CONFIG PARSE: found setting "+config['parameters']['parameter'][i]['name']+", "+config['parameters']['parameter'][i]['value']);
          	gs.info("CONFIG PARSE: looking for profile "+inputs.midName);
            // check for an existing profile
            profileGr.addQuery('name', inputs.midName);
            profileGr.query();
            // create a profile if one doesn't exist
            if (!(profileGr.next())) {
              	gs.info("CONFIG PARSE: profile "+inputs.midName+" not found, creating");
                profileGr.initialize();
                profileGr.name = inputs.midName;
                profileGr.insert();
            }
            // check if setting already exists
          	gs.info("CONFIG PARSE: looking for setting "+config['parameters']['parameter'][i]['name']+" under profile "+profileGr.sys_id);
            settingGr.addQuery('name', config['parameters']['parameter'][i]['name']);
            settingGr.addQuery('profile', profileGr.sys_id);
            settingGr.query();
            // if exists, check value
            if (settingGr.next()) {
              	gs.info("CONFIG PARSE: found existing setting "+config['parameters']['parameter'][i]['name']);
                if (settingGr.value != config['parameters']['parameter'][i]['value']) {
                    settingGr.value = sconfig['parameters']['parameter'][i]['value'];
                    settingGr.update();
                } 
            }
            else {
              	gs.info("CONFIG PARSE: creating new setting "+config['parameters']['parameter'][i]['name']);
               	settingGr.initialize();
             	settingGr.profile = profileGr.sys_id;
                settingGr.name = config['parameters']['parameter'][i]['name'];
                settingGr.value = config['parameters']['parameter'][i]['value'];
                settingGr.insert();
            }

    }
})(inputs, outputs);

